#

usage() {
    echo "Usage: ${0} [URL1 ... [URLn]] (optional)"
    # exit 0
    }
    
usage_url() {
    usage
    echo "When do not want to pass URL as parameter, please fill it within script beforehand!"
    exit 1
    }

incorrect_url() {
    usage
    echo "URL seems to be incorrect. Please correct!"
    exit 2
    }

# More than one URL can be used in single run
# Space delimited URLs
URLS=""
if [ $# -eq 0 ]
    then
        : # echo "No arguments supplied"
    else
        URLS="$*"
fi

if test -z "${URLS}"
then
    usage_url
fi

# Check correct URL
