user_agent="${LOGNAME}"@"$(hostname --long)"
declare -i TIMEOUT=5

CWD=$(pwd)
# echo "${CWD}"
DIR="$( cd "$( dirname "${BASH_SOURCE[0]}" )" >/dev/null 2>&1 && pwd )"
# echo "${DIR}"
SCRIPT=$(basename "${0}")
# echo "${SCRIPT}"

LOG="${DIR}"/"${SCRIPT}".log
# echo "${LOG}"

# CURL_PARAMS=("--silent" "--head")
CURL_PARAMS=("--silent")
if test -n "${TIMEOUT}"
then
    CURL_PARAMS=("${CURL_PARAMS[@]}" "--connect-timeout" "${TIMEOUT}")
fi
if test -n "${user_agent}"
then
    CURL_PARAMS=("${CURL_PARAMS[@]}" "--user-agent" "${user_agent}")
fi
# echo "${CURL_PARAMS[*]}"

echo "$(date "+[%F %T]") ===== Start ${0} =====" | tee --append "${LOG}"

for url in ${URLS}
do
    echo "url = ${url}"
    url_params=("${CURL_PARAMS[@]}")
    url_snipped=$(echo "${url}" | sed -e "s/https:\/\///g" | sed -e "s/http:\/\///g" | sed -e "s/:/_/g" | sed -e "s/\//_/g" | sed -e "s/_$//")
    # echo "url_snipped = ${url_snipped}"
    
    # url_log="${DIR}"/$(echo "${url_snipped}" | sed -e "s/$/\.log/")
    url_log="${DIR}"/"${url_snipped/%/\.log}"
    # echo "${url_log}"
    if test -n "${url_log}"
    then
        url_params=("${url_params[@]}" "--output" "${url_log}")
    fi
    
    # header_log="${DIR}"/$(echo "${url_snipped}" | sed -e "s/$/\.header/")
    header_log="${DIR}"/"${url_snipped/%/\.header}"
    # echo "header_log = ${header_log}"
    if test -n "${header_log}"
    then
        url_params=("${url_params[@]}" "--dump-header" "${header_log}")
    fi
    
    echo "$(date "+[%F %T]") /usr/bin/curl ${url_params[*]} ${url}" | tee --append "${LOG}"
    # set -o xtrace
    /usr/bin/curl "${url_params[@]}" "${url}" # > "${url_log}"
    # set +o xtrace
    # echo "Header of ${url}: ${header_log}"
    # echo "Log of ${url}: ${url_log}"
done

echo "$(date "+[%F %T]") ===== End of ${0} =====" | tee --append "${LOG}"
echo "Logfile: ${LOG}" | tee --append "${LOG}"
cd "${CWD}" || exit