#!/bin/bash
###################################################################
# Script Name   : wget.sh
# Script version: 1.3
# Script date   : 2022-01-25
# Description   : Download entire website(s) into local
# Args          : [${URL1}] ... [${URLn}] (optional)
# Author        : Toomas MÃ¶lder
# Email         : toomas.molder+wget@gmail.com
###################################################################

usage() {
    echo "Usage: ${0} [URL1 ... [URLn]] (optional)"
    # exit 0
    }
    
usage_url() {
    usage
    echo "When do not want to pass URL as parameter, please fill it within script beforehand!"
    exit 1
    }

incorrect_url() {
    usage
    echo "URL seems to be incorrect. Please correct!"
    exit 2
    }

# More than one URL can be used in single run
# Space delimited urls
urls=""
if [ ${#} -eq 0 ]
    then
        : # echo "No arguments supplied"
    else
        urls="$*"
fi

if test -z "${urls}"
then
    usage_url
fi

# Check correct URL
