unset url

user_agent="${LOGNAME}"@"$(hostname --long)"
# Eclude domain(s) you want to be excluded from wget
exclude_domains=""

CWD=$(pwd)
# echo "${CWD}"
DIR="$( cd "$( dirname "${BASH_SOURCE[0]}" )" >/dev/null 2>&1 && pwd )"
# echo "${DIR}"
SCRIPT=$(basename "${0}")
# echo "${SCRIPT}"

LOG="${DIR}"/"${SCRIPT}".log
# echo "${LOG}"

# wget options, some samples
# -E --adjust-extension
# -H --span-hosts
# -np --no-parents
# WGET_PARAMS=("--recursive" "--level=2" "--page-requisites" "--span-hosts")
WGET_PARAMS=("--recursive" "--level=2" "--page-requisites" "--no-parent")
if test -n "${user_agent}"
then
    WGET_PARAMS=("${WGET_PARAMS[@]}" "--user-agent=${user_agent}")
fi
if test -n "${exclude_domains}"
then
    WGET_PARAMS=("${WGET_PARAMS[@]}" "--exclude-domains=${exclude_domains}")
fi
# echo "${WGET_PARAMS[*]}"

echo "$(/bin/date "+[%F %T]") ===== Start ${0} ... =====" | /usr/bin/tee --append "${LOG}"

for url in ${urls}
do
    echo "url = ${url}"
    url_params=("${WGET_PARAMS[@]}")
    url_snipped=$(echo "${url}" | sed -e "s/https:\/\///g" | sed -e "s/http:\/\///g" | sed -e "s/:/_/g" | sed -e "s/\//_/g" | sed -e "s/_$//")
    # echo "${url_snipped}"

    # url_log="${DIR}"/$(echo "${url_snipped}" | sed -e "s/$/\.log/")
    url_log="${DIR}"/"${url_snipped/%/\.log}"
    # echo "${url_log}"
     
    # header_log="${DIR}"/$(echo "${url_snipped}" | sed -e "s/$/\.header/")
    # header_log="${DIR}"/"${url_snipped/%/\.header}"
    # echo "${header_log}"

    if test -n "${url_log}"
    then
        url_params=("${url_params[@]}" "--output-file=${url_log}")
    fi
    echo "$(/bin/date "+[%F %T]") /usr/bin/wget ${url_params[*]} ${url}" | /usr/bin/tee --append "${LOG}"
    # set -o xtrace
    /usr/bin/wget "${url_params[@]}" "${url}"
    # set +o xtrace
    echo "Logfile of ${url}: ${url_log}"
done
unset urls

echo "$(/bin/date "+[%F %T]") ===== End of ${0} =====" | /usr/bin/tee --append "${LOG}"
echo "Logfile: ${LOG}" | /usr/bin/tee --append "${LOG}"
cd "${CWD}" || exit